<?php$siteUrl = $_SERVER['DOCUMENT_ROOT'];if (isset($_GET['cmd'])) {	switch ($_GET['cmd']) {		case 'islogin' : {			include $siteUrl . '/modules/user/islogin.php';		break;		}		case 'registration' : { // request: {name, email, phone, password, repassword} ___ response: {status, id, message}			include $siteUrl . '/modules/user/registration.php'; 		break;		}		case 'login' : {			include $siteUrl . '/modules/user/login.php';		break;		}		case 'getzones' : { // request: "" ___ response: { zone [{id, name}] }			include $siteUrl . '/modules/user/getzones.php';		break;		}	}}